import styled from "@emotion/styled";
import PokeMarkChip from "../components/PokeMarkChip";
import {
  fetchPokemonDetail,
  PokemonDetailType,
} from "../Service/PokemonService";
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const TempImgUrl =
  "https://upload.wikimedia.org/wikipedia/ko/thumb/a/a6/Pok%C3%A9mon_Pikachu_art.png/200px-Pok%C3%A9mon_Pikachu_art.png";

const PokemonDetail = () => {
  const { name } = useParams();
  const [pokemon, setPokemon] = useState<PokemonDetailType | null>(null);

  useEffect(() => {
    if (!name) {
      return;
    }
    (async () => {
      const detail = await fetchPokemonDetail(name);
      setPokemon(detail);
    })();
  }, [name]);

  if (!name || !pokemon) {
    return null;
  }

  return (
    <Container>
      <ImageContainer>
        <Image src={pokemon.images.dreamWorldFront} alt={pokemon.koreanName} />
      </ImageContainer>
      <Divider />
      <Body>
        <h2>기본정보</h2>
        <Table>
          <TableRow>
            <TableHeader>번호</TableHeader>
            <td>1</td>
          </TableRow>
          <TableRow>
            <TableHeader>이름</TableHeader>
            <td>이상해씨</td>
          </TableRow>
        </Table>
        <h2>능력치</h2>
        <Table>
          <TableRow>
            <TableHeader>hp</TableHeader>
            <td>45</td>
          </TableRow>
          <TableRow>
            <TableHeader>attack</TableHeader>
            <td>49</td>
          </TableRow>
        </Table>
      </Body>
      <Footer>
        <PokeMarkChip />
      </Footer>
    </Container>
  );
};

const Container = styled.section`
  border: 1px solid #c0c0c0;
  margin: 16px 32px;
  border-radius: 16px;
  box-shadow: 1px 1px 3px 1px #c0c0c0;
`;

const ImageContainer = styled.section`
  display: flex;
  flex: 1 1 auto;
  justify-content: center;
  align-items: center;
  margin: 8px 0;
`;

const Image = styled.img`
  width: 350px;
  height: 350px;
`;

const Divider = styled.hr`
  margin: 32px;
  border-style: none;
  border-top: 1px dashed #d3d3d3;
`;

const Body = styled.section`
  margin: 0 32px;
`;

const Table = styled.table`
  width: 100%;
  border-collapse: collapse;
  border-spacing: 0;
  margin: 0 auto 12px;

  td,
  td {
    padding: 6px 12px;
  }
`;

const TableRow = styled.tr`
  border-width: 1px 0;
  border-style: solid;
  border-color: #c0c0c0;
`;

const TableHeader = styled.th`
  width: 1px;
  white-space: nowrap;
  text-align: left;
  font-weight: normal;
  font-size: 14px;
  color: #c0c0c0;
`;

const Footer = styled.section`
  display: flex;
  flex-direction: row;
  margin: 32px 16px;
`;

export default PokemonDetail;
