import styled from "@emotion/styled";
import { Link } from "react-router-dom";
import { POKEMON_IMAGE_TYPE } from "../Constant";
import { useSelector } from "react-redux";
import { RootState, useAppDispatch } from "../Store";
import { ChangeEvent, useState } from "react";
import { changeImageType, PokemonImageKeyType } from "../Store/imageTypeSlice";
import { setSearchQuery } from "../Store/searchSlice"; // 검색 슬라이스 import

const PageHeader = () => {
  const type = useSelector((state: RootState) => state.imageType.type);
  const dispatch = useAppDispatch();

  const [searchInput, setSearchInput] = useState(""); // 로컬 상태로 검색어 관리

  const handleChange = (e: ChangeEvent<HTMLSelectElement>) => {
    dispatch(
      changeImageType({
        type: e.target.value as PokemonImageKeyType,
      })
    );
  };

  const handleSearchChange = (e: ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value;
    setSearchInput(value); // 로컬 상태 업데이트
    dispatch(setSearchQuery(value)); // Redux로 검색어 전달
  };

  return (
    <Header>
      <Title>
        <Link to="/">Pokémon</Link>
      </Title>
      <SearchInput
        type="text"
        placeholder="Search Pokémon"
        value={searchInput}
        onChange={handleSearchChange}
      />
      <Select value={type} onChange={handleChange}>
        <option value={POKEMON_IMAGE_TYPE.OFFICIAL_ARTWORK}>official</option>
        <option value={POKEMON_IMAGE_TYPE.DREAM_WORLD}>DreamWorld</option>
        <option value={POKEMON_IMAGE_TYPE.FRONT_DEFAULT}>FrontDefault</option>
      </Select>
    </Header>
  );
};

const Header = styled.nav`
  display: flex;
  padding: 16px 32px;
  margin-bottom: 16px;
  border-bottom: 1px solid #c0c0c0;
  align-items: center;
`;

const Title = styled.h1`
  margin: 0;
  font-size: 32px;
  color: #ffca09;
  text-shadow: 1px 0 blue, 0 2px blue, 1px 0 blue, 0 1px blue;
`;

const SearchInput = styled.input`
  margin-left: 16px;
  padding: 8px;
  flex: 1; // 남은 공간 채우기
  border-radius: 8px;
  border: 1px solid #c0c0c0;
  font-size: 16px;
`;

const Select = styled.select`
  margin-left: 16px;
  padding: 8px 12px;
  border-radius: 8px;
`;

export default PageHeader;
